INSERT INTO `db_problem` VALUES ('10024', '2016-05-10 11:57:48', '2016-05-11 16:52:03', '15011992855', '1.工作页面划卡记录重复的问题，正在处理；\r\n2.请检查网络原因看看；\r\n3.查看提供的几个员工的账号，都是绑定美容院的。', '新版2.4.5', '', '普通BUG', '29', '刘洁兰', '新版本，老板帐号15011992855,密码992855.其中一个员工帐号 13751718364。三个问题：1、工作页面很多划卡记录是重复的。2、店里所有员工写护理日志或者日总结保存时，经常转圈然后内容就不见了。3、很多员工已经绑定美容院，已经开始使用的帐号，突然自动退出跳到绑定美容院页面，并显示升级经理版。出现频率：都很频繁。', '10024.png', '已授权', '苹果，安卓', '必现', '普通员工', '', 'STATUS_SUCCESS', '护理日志和日总结保存时缓存太慢', '144', '杨鑫', 'IOS', null);
